import React, { Fragment } from 'react'
import css from './gradesControl.module.css';

export default function GradesControl({ grades, onDelete, onPersist }) {

    let tableGrades = [];

    let currentStudent = grades[0].student
    let currentSubject = grades[0].subject
    let currentGrades = []
    let id = 1;
    let i = 0;
    console.log('currentStudent: ' + currentStudent);
    console.log('currentSubject: ' + currentSubject);
    console.log('currentGrades ' + currentGrades);
    grades.forEach(grade => {
        i++;
        console.log('inicio do loop: ' + i);
        console.log('grade.subject: ' + grade.subject);
        console.log('type: ' + typeof(grade.subject));
        console.log('currentSubject: ' + currentSubject);
        console.log('type: ' + typeof(currentSubject));
        console.log('São diferentes? ' + (grade.subject !== currentSubject));
        if (grade.suject !== currentSubject) {
            console.log('caiu no if: ' + i);

            tableGrades.push({
                id: id++,
                student: currentStudent,
                subject: currentSubject,
                grades: currentGrades,
            });
            currentSubject = grade.subject;
            currentGrades = [];
        }

        if (grade.student !== currentStudent) {
            currentStudent = grade.student;
        }
        currentGrades.push(grade);
    });

    console.log('Table Grades');
    console.log(tableGrades);

    return (
        <Fragment>
            <div>
                <table className={'striped'} >
                    <thead style={{ alignItems: 'center' }}>
                        <tr>
                            <th>Aluno</th>
                            <th>Disciplina</th>
                            <th>Avaliação</th>
                            <th>Nota</th>
                            <th>&nbsp;</th>
                            <th>&nbsp;</th>
                        </tr>
                    </thead>
                    <tbody>
                        {grades.map(({ id, student, subject, type, value, isDeleted }) => {
                            return (
                                <tr key={id}>
                                    <td>{student}</td>
                                    <td>{subject}</td>
                                    <td>{type}</td>
                                    <td>{value}</td>
                                    <td>&nbsp;</td>
                                    <td>&nbsp;</td>
                                </tr>
                            );
                        })

                        }
                    </tbody>
                    <tfoot>

                    </tfoot>
                </table>
            </div>
        </Fragment>
    )
}
