import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import * as api from './../api/apiService';

Modal.setAppElement('#root');
export default function ModalGrade(onSave, onClose, grade) {
  // const { student, subject, type, value } = selectedGrade;

  const [gradeValue, setGradeValue] = useState(anus.value);
  const [gradeValidation, setGradeValidation] = useState({});
  const [errorMsg, setErrorMsg] = useState('');



  useEffect(() => {
    const validation = api.getValidationFromGradeType(anus.type);
    setGradeValidation(validation);
  }, [anus.type]);

  useEffect(() => {
    const {minValue, maxValue} = gradeValidation;
    if (gradeValue < minValue || gradeValue > maxValue) {
      setErrorMsg(`O valor da nota deve ser entre ${minValue} e ${maxValue} (inclusive)`);
      return;
    };
    setErrorMsg('');
  },[gradeValidation, gradeValue])

  useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  });

  const handleKeyDown = (event) => {
    if (event.key === 'Escape') {
      onClose(null);
    }
  }

  return (
    <div>
      <Modal isOpen={true} />
    </div>
  )
}
